import React, { useState, useEffect } from 'react';
import Stack from '@mui/material/Stack'; // Ensure you have the correct import for Stack
import AlumniTable from './AlumniTable'; // Adjust the path as necessary
import StaffTable from './StaffTable'; // Adjust the path as necessary

const ParentComponent = ({ tabIndex }) => {
  const [alumniMeetingData, setAlumniMeetingData] = useState(null);
  const [staffMeetingData, setStaffMeetingData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Function to fetch both data sets
    const fetchData = async () => {
      try {
        const [alumniResponse, staffResponse] = await Promise.all([
          fetch('/api/alumni-meetings'), // Replace with your actual endpoint
          fetch('/api/staff-meetings') // Replace with your actual endpoint
        ]);

        const alumniData = await alumniResponse.json();
        const staffData = await staffResponse.json();

        setAlumniMeetingData(alumniData);
        setStaffMeetingData(staffData);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!alumniMeetingData || !staffMeetingData) {
    return <div>No data available</div>;
  }

  return (
    <Stack spacing={2} alignItems="center" sx={{ marginTop: "20px" }}>
      {(tabIndex === 0 && alumniMeetingData) && (
        <AlumniTable alumniData={alumniMeetingData} />
      )}
      {(tabIndex === 1 && staffMeetingData) && (
        <StaffTable staffData={staffMeetingData} />
      )}
    </Stack>
  );
};

export default ParentComponent;
