import Grid from "@mui/material/Grid";
import Divider from "@mui/material/Divider";
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import DashboardNavbar from "examples/Navbars/DashboardNavbar";
import Footer from "examples/Footer";
import Header from "layouts/profile/components/Header";
import PlatformSettings from "layouts/profile/components/PlatformSettings";
import MDButton from "components/MDButton";
import { useState } from "react";

function Overview() {
  const [isEditMode, setIsEditMode] = useState(false);

  const handleEditClick = () => {
    setIsEditMode(!isEditMode);
  };

  return (
    <DashboardLayout>
      <DashboardNavbar />
      <Header title="Profile" subtitle="Manage your profile settings" />
      <Box p={3}>
        <Grid container spacing={3}>
          <Grid item xs={12} md={6}>
            <Box sx={{ display: "flex", alignItems: "center" }}>
              <Divider orientation="vertical" flexItem />
              <Box ml={2}>
                <TextField
                  fullWidth
                  id="id"
                  label="ID"
                  disabled={!isEditMode} // Disable input based on edit mode
                  variant="outlined"
                  defaultValue="12345"
                  sx={{ marginBottom: "20px" }}
                />
                <TextField
                  fullWidth
                  id="date-of-joining"
                  label="Date of Joining"
                  type="date"
                  disabled={!isEditMode} 
                  variant="outlined"
                  defaultValue="2024-03-24" // Replace with actual value
                  InputLabelProps={{
                    shrink: true,
                  }}
                  sx={{ marginBottom: "20px" }}
                />
                <TextField
                  fullWidth
                  disabled={!isEditMode} 
                  id="date-of-birth"
                  label="Date of Birth"
                  type="date"
                  variant="outlined"
                  defaultValue="1990-01-01" // Replace with actual value
                  InputLabelProps={{
                    shrink: true,
                  }}
                  sx={{ marginBottom: "20px" }}
                />
                <TextField
                  fullWidth
                  id="phone"
                  disabled={!isEditMode} 
                  label="Phone No."
                  variant="outlined"
                  defaultValue="123-456-7890"
                  sx={{ marginBottom: "20px" }} // Replace with actual value
                />
                <TextField
                  fullWidth
                  id="email"
                  label="Email"
                  disabled={!isEditMode} 
                  variant="outlined"
                  defaultValue="example@example.com"
                  sx={{ marginBottom: "20px" }} // Replace with actual value
                />
                <TextField
                  fullWidth
                  id="address"
                  label="Address"
                  disabled={!isEditMode} 


                  multiline
                  rows={4}
                  variant="outlined"
                  defaultValue="123 Street, City, Country"
                  sx={{ marginBottom: "20px" }} // Replace with actual value
                />
              </Box>
            </Box>
            <Box sx={{ marginLeft: '600px' }}> {/* Move the box to the right */}
            <MDButton color="info" onClick={handleEditClick}>{isEditMode ? 'Save' : 'Edit'}</MDButton>
            </Box>
          </Grid>
        </Grid>
      </Box>
      <Footer />
    </DashboardLayout>
  );
}

export default Overview;
